function downloadingEvent(a){console.log("there are new resources");var b=$("<p id='cacheupdate' />").addClass('overlay');var c=$("<div id='innerdiv' />").html("A new version of PyRT is downloading");b.append(c);$("body",window.parent.document).css({"overflow":"hidden","position":"fixed"}).prepend(b);}function updatereadyEvent(a){console.log("got everything, should refresh now");$("#innerdiv",window.parent.document).html("A new version of PyRT is available, click <a href='/' alt='Refresh' title='Refresh'>here</a> to load it");}var appCache=window.applicationCache;appCache.ondownloading=downloadingEvent;appCache.onupdateready=updatereadyEvent;