function downloadingEvent(a){console.log("there are new resources");a=$("<p id='cacheupdate' />").addClass("overlay");var b=$("<div id='innerdiv' />").html("A new version of PyRT is downloading");a.append(b);$("body",window.parent.document).css({overflow:"hidden",position:"fixed"}).prepend(a)}
function updatereadyEvent(a){console.log("got everything, should refresh now");$("#innerdiv",window.parent.document).html("A new version of PyRT is available, click <a href='"+window.location.href+"/..' alt='Refresh' title='Refresh'>here</a> to load it")}var appCache=window.applicationCache;appCache.ondownloading=downloadingEvent;appCache.onupdateready=updatereadyEvent;appCache.oncached=updatereadyEvent;
